import  UserActionTypes  from './user.types';

export const setCurrentUser = user => ({
    type: UserActionTypes.SET_CURRENT_USER,
    payload: user,
})

export const googleSignInStart = () => {
    return ({
        type: UserActionTypes.GOOGLE_SIGNIN_START,
    })
}

export const googleSingInSuccess = (user) => {
    return ({
        type: UserActionTypes.GOOGLE_SIGNIN_SUCCESS,
        payload: user
    })
}

export const googleSingInFailure = (error) => {
    return ({
        type: UserActionTypes.GOOGLE_SIGNIN_FAILURE,
        payload: error,
    })
}

export const emailSingInStart = emailAndPassword => {
    return ({
        type: UserActionTypes.EMAIL_SIGNIN_START,
        payload: emailAndPassword
    })
}

export const emailSingInSuccess = emailAndPassword => {
    return ({
        type: UserActionTypes.EMAIL_SIGNIN_SUCCESS,
        payload: emailAndPassword,
    })
}

export const emailSingInFailure = (error) => {
    return ({
        type: UserActionTypes.EMAIL_SIGNIN_FAILURE,
        payload: error,
    })
}
