import shopActionTypes from './shop.types';

export const updateCollections = collectionsMap => {
    return ({
        type: shopActionTypes.UPDATE_COLLECTIONS,
        payload: collectionsMap,
    })

}
//thunk is action creator/function that returns dispatch
