import React from 'react';
import './Login.styles.scss';



export default class Login extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            email: '',
            password: '',
        }
    }

    handleSubmit = (e) => {
        e.preventDefault();
        this.setState({email: '', password:'',})
    }

    handleChange =(e)=> {
        e.preventDefault();
        const email = e.target.name;
        const password = e.target.name;
        console.log(e.target.name, password.value)
    }
    render () {
        return (
            <div className='sign-in'>
                <h2>I already have an account</h2>
                <span>Sign in with your email and password</span>

                <form onSubmit={this.handleSubmit}>
                    <input name='email' value={this.state.email} onChange={this.handleChange} required />
                    <label>Email</label>
                    <input name='password' value={this.state.password} onChange={this.handleChange} required />
                    <label>Password</label>

                    <input type='submit' value='submit form' required />
                </form>
            </div>
        )
    }
}
