import React from 'react';
import './StripeButton.styles.scss';
import StripeCheckout from 'react-stripe-checkout';
// import axios from 'axios';
import config from '../../config';


const StripeCheckoutButton = ({price}) => {
    const priceForStripe = price * 100 //stripe needs to get the price value in cents
    const publishableKey = 'pk_test_Uv2WNYGHsgq7vXy7n40tTEn800RtkKKA7l'

    const onToken = token => {
        const url = config.API_ENDPOINT + 'payment';
        console.log(token, url); //carries info about the payment to stripe
        fetch(url, {
            method: 'post',
            body: {
                amount: priceForStripe,
                token
            }
        })
        .then(res => {
            alert('Payment Successful')
            return res;
        })
        .then(res => res.json())
        .catch(error => {
            console.log(error.message)
            alert('There was an issue with your payment')
        })

    }
    return (
        //from stipe docs we can choose whatever features to include here as props
        <StripeCheckout 
            label='Pay Now'
            name='CROWN Clothing LTD.'
            billingAddress
            shippingAddress
            image =''
            description={`Your total is $${price}`}
            amount={priceForStripe}
            panelLabel='Pay Now'
            token={onToken}
            stripeKey={publishableKey}
        />

    )
}
export default StripeCheckoutButton;
