import shopActionsType from './shop.types';

const INITIAL_STATE = {
    collections: null,
}

const shopReducer = (state =INITIAL_STATE, action) => {

    switch(action.type) {
        case shopActionsType.UPDATE_COLLECTIONS:
          return {
            ...state,
            collections: action.payload
          } 

          default:
            return state;
    }
}

export default shopReducer;
