import { takeEvery, call } from 'redux-saga/effects';
import ShopActionTypes from '../shop/shop.types';
import { firestore, convertCollectionSnapshotToMap } from '../../firebase/firebase.util';
import { fetchCollectionsSuccess, fetchCollectionsFailure } from '../shop/shop.actions';


export function* fetchCollectionsAsync() {
    yield console.log('im fired!!!!!')

    try {
        const collectionRef = firestore.collection('collections')
        const snapshot = yield collectionRef.get();
        const collectionMap = yield call(convertCollectionSnapshotToMap, snapshot) //using yield call to make sure it will get finished before following action gets triggered, call takes as arguments function and its parameters  
        //sagas use method PUT to trigger actions (instead of dispatch)
    } catch(error) {

    }

    // collectionRef
    // .get()
    // .then(snapshot => {
    //     const collectionsMap = convertCollectionSnapshotToMap(snapshot)
    //     dispatch(fetchCollectionsSuccess(collectionsMap)) 
    // }) 
    // .catch(err => {
    //     return dispatch(fetchCollectionsFailure(err.message))
    // })

}
export function* fetchCollectionsStart() {
    yield takeEvery(ShopActionTypes.FETCH_COLLECTIONS_START, fetchCollectionsAsync)
}
